let time = 0;
let wave = [];

function setup() {
  createCanvas(600, 400);
  time = PI/2
}

function draw() {
  background(55);
  translate(150, 200);

  let x = 0;
  let y = 0;

  for (let i = 0; i < 5; i++) {
    let prevx = x;
    let prevy = y;

    let n = i * 2 + 1;
    let r = 8 * ((4 / n) * PI);
    x += r * cos(n * time);
    y += r * sin(n * time);


    stroke(255, 100);
    noFill();
    ellipse(prevx, prevy, r * 2);

    fill(255);
    stroke(255)
    line(prevx, prevy, x, y);
    ellipse(x, y, 5);

    prevx = x;
    prevy = y
  }
  wave.unshift(y);

  translate(150, 0);
  line(x - 150, y, 0, wave[0]);
  beginShape();
  noFill();
  for (let i = 0; i < wave.length; i++) {
    vertex(i, wave[i]);
  }
  endShape();

  time += 0.02;

  if (wave.length > 500) {
    wave.pop();
  }
}

