class Walker {
  constructor(x, y, r) {
    this.pos = createVector(x, y);
    this.vel = p5.Vector.random2D().mult(random(2));
    this.acc = createVector(0, 0);
    this.r = r;
    this.mass = PI* sq(this.r) * 0.0001
    this.fillColor = undefined;
    this.changeFillColor()
    if (r < 20) {
      this.dsize = random(0.1);
    } else {
      this.dsize = -random(0.1);
    }
    this.dragC = 0.00001
  }

  applyForce(force) {
    this.acc.add(force);
  }

  edges() {
    let onEdge = false
    if (this.pos.y >= height - this.r) {
      this.pos.y = height - this.r;
      this.changeFillColor();
      this.vel.y *= -1;
      onEdge = true;
    }
    if (this.pos.y <= this.r) {
      this.pos.y = this.r;
      this.changeFillColor();
      this.vel.y *= -1;
      onEdge = true;
    }

    if (this.pos.x >= width - this.r) {
      this.pos.x = width - this.r;
      this.changeFillColor();
      this.vel.x *= -1;
      onEdge = true;
    }
    if (this.pos.x <= this.r) {
      this.pos.x = this.r;
      this.changeFillColor();
      this.vel.x *= -1;
      onEdge = true;
    }
    if (onEdge) {
      this.vel.mult(0.9)
    }
    return onEdge;
  }

  drag() {
    // Direction of Drag
    let drag = this.vel.copy();
    drag.normalize();
    drag.mult(-1);
    let speedSq = this.vel.magSq();
    drag.setMag(this.dragC * speedSq*PI*this.r);

    this.applyForce(drag);
  }

  update() {
    this.drag()
    this.vel.add(this.acc);
    this.pos.add(this.vel);
    this.edges()

    this.acc.set(0, 0);

    if (this.r > 35 || this.r < 5) {
      this.dsize *= -1;
    }
    this.r += this.dsize;
  }

  changeFillColor() {
    this.fillColor = color(random(255), random(255), random(255), 200);
  }

  show() {
    strokeWeight(2);
    noStroke(255);
    fill(this.fillColor);
    ellipse(this.pos.x, this.pos.y, this.r * 2);
  }
}

