class Snake {
  constructor() {
    this.body = [];
    this.body[0] = createVector(0, floor(h / 2));
    this.xdir = 1;
    this.ydir = 0;
  }

  update() {
    let head = this.body[this.body.length - 1].copy();
    head.x += this.xdir;
    head.y += this.ydir;
    if (head.x > floor(width/res) -1) {
      head.x = 0;
    } else if (head.y > floor(height/res) -1) {
      head.y = 0;
    } else if (head.x < 0) {
      head.x = floor(width/res);
    } else if (head.y < 0) {
      head.y = floor(height/res);
    }
    this.body.shift();
        this.body.push(head);

  }
  score() {
    return this.body.length - 1;
  }
  show() {
    strokeWeight(0.1)
    stroke(253, 231, 76)
    for (let i = 0; i < this.body.length; i++) {
      fill(60, 125, 120);
      if (i == this.body.length - 1) {
        fill(0);
      }
      rect(this.body[i].x, this.body[i].y, 1, 1);
    }
  }
  resetSnake() {
    this.body.splice(0, this.body.length);
    this.body[0] = createVector(0, floor(h / 2));
    this.xdir = 1;
    this.ydir = 0;
  }

  endGame() {
    let x = this.body[this.body.length - 1].x;
    let y = this.body[this.body.length - 1].y;
    for (let i = 0; i < this.body.length - 1; i++) {
      let part = this.body[i];
      if (part.x == x && part.y == y) {
        this.resetSnake();
        return true;
      }
    }
    return false;
  }

  eat(pos) {
    let x = this.body[this.body.length - 1].x;
    let y = this.body[this.body.length - 1].y;
    if (x === pos.x && y === pos.y) {
      this.grow();
      return true;
    } else {
      return false;
    }
  }
  grow() {
    let head = this.body[this.body.length - 1].copy();
    this.body.push(head);
  }
  setDir(x, y) {
    if (
      (this.xdir == 1 && x == -1) ||
      (this.xdir == -1 && x == 1) ||
      (this.ydir == 1 && y == -1) ||
      (this.ydir == -1 && y == 1)
    ) {
      return;
    }
    this.xdir = x;
    this.ydir = y;
  }
}

