let keys;
let keyboard;

let row1List = ["tab", "q", "w", "e", "r", "t", "y", "u", "i", "o", "p"];
let row2List = ["capslock", "a", "s", "d", "f", "g", "h", "j", "k", "l", ";"];
let row3List = ["shift", "z", "x", "c", "v", "b", "n", "m", ",", ".", "/"];
let row4List = [
  "control",
  "meta",
  "alt",
  "space",
  "alt",
  "contextmenu",
  "control",
];

let cursorVisible = false

let typedText = "";

function setup() {
  createCanvas(windowWidth, windowHeight);
  keys = {
    // first row
    tab: new Key("Tab", 1.7, false),
    q: new Key("Q", 1),
    w: new Key("W", 1),
    e: new Key("E", 1),
    r: new Key("R", 1),
    t: new Key("T", 1),
    y: new Key("Y", 1),
    u: new Key("I", 1),
    i: new Key("O", 1),
    o: new Key("U", 1),
    p: new Key("P", 1),

    // second row
    capslock: new Key("Caps", 2.1, false),
    a: new Key("A", 1),
    s: new Key("S", 1),
    d: new Key("D", 1),
    f: new Key("F", 1),
    g: new Key("G", 1),
    h: new Key("H", 1),
    j: new Key("J", 1),
    k: new Key("K", 1),
    l: new Key("L", 1),
    ";": new Key(";", 1),

    // third row
    shift: new Key("Shift", 2.8),
    z: new Key("Z", 1),
    x: new Key("X", 1),
    c: new Key("C", 1),
    v: new Key("V", 1),
    b: new Key("B", 1),
    n: new Key("N", 1),
    m: new Key("M", 1),
    ",": new Key(",", 1),
    ".": new Key(".", 1),
    "/": new Key("/", 1),

    // row 4
    control: new Key("Ctrl", 1.7),
    meta: new Key("Meta", 1.3),
    alt: new Key("Alt", 1.3),
    space: new Key("Space", 5),
    contextmenu: new Key("Menu", 2.1),
  };

  let row1 = row1List.map((r) => keys[r]);
  let row2 = row2List.map((r) => keys[r]);
  let row3 = row3List.map((r) => keys[r]);
  let row4 = row4List.map((r) => keys[r]);

  keyboard = new Keyboard([row1, row2, row3, row4], 25, 50, 40);
}

function keyPressed() {
  if (key === "CapsLock") {
    keys.capslock.toggle();
  }
  let pressedKey = key.toLowerCase();
  if (key === " ") {
    pressedKey = "space";
  }

  if (key === "Backspace")  {
    typedText = typedText.slice(0, -1)
  }

  if (!(pressedKey in keys)) {
    print(key);
    return;
  }
  keys[pressedKey].press();
  typedText += key;
}

function keyReleased() {
  let pressedKey = key.toLowerCase();
  if (key === " ") {
    pressedKey = "space";
  }
  if (!(pressedKey in keys)) {
    print(key);
    return;
  }
  keys[pressedKey].release();
}

function draw() {
  background(22);
  keyboard.show();
  fill(255);
  const typedTextWidth = textWidth(typedText)
  text(typedText, 0, height - 20);
  if (random() < 0.05) {
    cursorVisible = true
  }
  if (random() < 0.05) {
    cursorVisible = false
  }

  if (!cursorVisible) {
    noFill()
  }
  text("I", typedTextWidth + 5 , height - 20);
}

