class Keyboard {
  constructor(keyRows, x, y, h) {
    this.keyRows = keyRows;
    this.x = x;
    this.y = y;
    this.h = h;
    this.offset = 10;
    this.w = 25;

    // calculate width of each key
    let maxColsWidth = 0;
    let maxCols = 0;
    for (let cols of keyRows) {
      let nCols = 0;
      for (let k of cols) {
        nCols += k.wScale;
      }
      if (nCols > maxColsWidth) {
        maxColsWidth = nCols;
        maxCols = cols.length;
      }
    }
    print(maxColsWidth, maxCols);

    let totalOffsetWidth = maxCols * this.offset;
    let w = (width - totalOffsetWidth - this.x * 2) / maxColsWidth;
    this.w = w;
  }
  show() {
    // Go over every rows
    push()
    translate(this.x, this.y);
    for (let i = 0; i < this.keyRows.length; i++) {
      const cols = this.keyRows[i];
      let x = 0;
      for (let j = 0; j < cols.length; j++) {
        const k = cols[j];

        const w = k.wScale * this.w;
        let white = 255;
        if (!k.active) {
          white = 155;
        }

        if (k.isPressed) {
          fill(255);
          stroke(0);
        } else {
          stroke(255);
          noFill();
        }
        rect(x, i * this.h + i * this.offset, w, this.h);
        if (k.isPressed) {
          fill(0);
          } else {
          fill(white);
        }
        noStroke()
        textStyle(BOLD )
        text(
          k.label,
          x + w / 2 - 5,
          i * this.h + i * this.offset + this.offset * 2.25
        );
        x += k.wScale * this.w + this.offset;
      }
    }
    pop()
  }
  update() {}
}

class Key {
  constructor(label, wScale, active = true) {
    this.label = label;
    this.wScale = wScale;
    this.active = active;
    this.isPressed = false;
  }

  press() {
    if (this.active) {
      this.isPressed = true;
    }
  }
  toggle() {
    this.isPressed = !this.isPressed;
  }
  release() {
    if (this.active) {
      this.isPressed = false;
    }
  }

  show() {}
  update() {}
}

let projection = [
  [1, 0, 0], 
  [0, 1, 0]
];

let points = [
  [100],
  [75],
  [50]
]
function setup() {
  createCanvas(400, 400);
  printMat(projection)
  printMat(points)
  printMat(matMul(projection, points))
}

function draw() {
  background(220);
}

function matMul(a, b) {
  let colsA = a[0].length
  let rowsA = a.length

  let colsB = b[0].length
  let rowsB = b.length

  if (colsA != rowsB) {
    console.log("Columns of A must equals Rows of B")
    return null
  }

  const results = Array.from(Array(rowsA), () => new Array(colsB))  
  console.log(results)
  // return null
  for (let i = 0; i < rowsA; i++) {
    for (let j = 0; i < colsB; j++) {
      let sum = 0;
      for (let k = 0; k < colsA; k++) {
        sum+= a[i][k] * b[k][j];
      }
      results[i][j] = sum
    }
  }

  return results
}

function printMat(m) {
  let cols = m[0].length
  let rows = m.length

  print(rows + " X " + cols)
  print("-----------------")
  for (let i = 0; i < rows; i++) {
    let s = ""
    for (let j = 0; j < cols; j++) {
      s += m[i][j] + " "
    }
    console.log(s)
  }
  console.log("\n")
}

