let x = 0.01;
let y = 0;
let z = 0;

let a = 10;
let b = 28;
let c = 8/3;

const points = []

function setup() {
  createCanvas(windowWidth, windowHeight, WEBGL);
}

function draw() {
  background(20)

  let dt = 0.01;
  let dx = (a * (y - x)) * dt;
  let dy = (x * (b - z) - y) * dt;
  let dz = (x * y - c * z )* dt;
  x += dx;
  y += dy;
  z += dz;

  points.push(createVector(x, y, z))

  // translate(x, y, z)
  scale(5)
  // camera(400*(frameCount * 0.01), 800, 0)
  rotateY(frameCount * 0.01)
  stroke(255)
  strokeWeight(10)
  point(x, y, z)
  strokeWeight(2)

  noFill()
  beginShape()
  for (const p of points) {
    vertex(p.x, p.y, p.z)
  }
  endShape()
  if (points.length  > 100) {
    points.shift()
  }
  // print(x, y, z)
}

