  frameRate(1)
}

function draw() {
  background(51);
  tree = new Tree()
  for (let i = 0; i < 20; i++) {
    tree.insert(Math.floor(Math.random() * 100));
  };
  tree.traverse();

}

class Tree {
  constructor() {
    this.root = null;
  }
  insert(value) {
    const n = new Node(value);
    if (this.root == null) {
      this.root = n;
      this.root.x = width / 2;
      this.root.y = s*2;
    } else {
      this.root.insertNode(n);
    }
  }
  search(value) {
    return this.root.search(value);
  }
  traverse() {
    this.root.visit(null);
  }
}
class Node {
  constructor(value, x, y) {
    this.value = value;
    this.x = x;
    this.y = y;
    this.left = null;
    this.right = null;
  }
  search(value) {
    if (this.value === value) {
      console.log("Found: ", value);
      return true;
    } else if (value < this.value && this.left != null) {
      return this.left.search(value);
    } else if (value > this.value && this.right != null) {
      return this.right.search(value);
    }
    return false;
  }
  visit(parent) {
    if (this.left != null) {
      this.left.visit(this);
    }
    console.log(this.value);
    fill(255);
    stroke(255);
    textAlign(CENTER);
    text(this.value, this.x, this.y);
    noFill();
    ellipse(this.x, this.y - s*0.15,s, s);
    if (parent) {
      line(parent.x, parent.y + s *0.35 , this.x, this.y - s * 0.65);
    }
    if (this.right != null) {
      this.right.visit(this);
    }
  }
  insertNode(n) {
    if (n.value < this.value) {
      if (this.left == null) {
        this.left = n;
        this.left.x = this.x - s;
        this.left.y = this.y + s*2;
      } else {
        this.left.insertNode(n);
      }
    } else if (n.value > this.value) {
      if (this.right == null) {
        this.right = n;
        this.right.x = this.x + s;
        this.right.y = this.y + s*2;
      } else {
        this.right.insertNode(n);
      }
    }
  }
}

