let boids = [];
let dir = 1;
function setup() {
  createCanvas(windowWidth, windowHeight);
  let boid = new Boid(50, 50, 0, "red");
  boids.push(boid);
  colorMode(HSL)
}

function draw() {
  background(90);
  for (let boid of boids) {
    boid.show();
    boid.update();
    boid.angle += random(0.01*dir)
  }

  if (random() < 0.01) {
    dir *= -1;
  }

  if (random() < 0.1) {
    boids.push(
      new Boid(
        randomPos()[0],
        randomPos()[1],
        randomPos()[2],
        color(0, random(40, 80), random(40, 60))
      )
    );
  }
}

function randomPos() {
  let edge = int(random(4)); // Randomly choose one of the four edges
  let x, y;
  const offset = 50; // Distance outside the screen

  switch (edge) {
    case 0: // Top edge
      x = random(-offset, width + offset);
      y = -offset;
      break;
    case 1: // Right edge
      x = width + offset;
      y = random(-offset, height + offset);
      break;
    case 2: // Bottom edge
      x = random(-offset, width + offset);
      y = height + offset;
      break;
    case 3: // Left edge
      x = -offset;
      y = random(-offset, height + offset);
      break;
  }

  let a = createVector(x, y).angleBetween(createVector(width / 2, height / 2));
  return [x, y, a];
}

