class Boid {
  constructor(x, y, angle, c) {
    this.pos = createVector(x, y);
    this.color = c;
    this.angle = angle;
    this.speed = 5;
    this.tall = 60;
    this.fat = 20;
  }

  show() {
    let dir = p5.Vector.fromAngle(this.angle);
    let a = p5.Vector.sub(this.pos, p5.Vector.mult(dir, 100));
    let b = p5.Vector.mult(dir, 150);

    push();
    translate(this.pos.x, this.pos.y);
    rotate(this.angle);
    noStroke();
    colorMode(HSL);
    fill(this.color);
    triangle(0, 0, -this.tall, this.fat, -this.tall, -this.fat);
    pop();
    stroke(0);
    line(a.x, a.y, b.x + a.x, b.y + a.y);
  }
  update() {
    let dir = p5.Vector.fromAngle(this.angle);
    dir.mult(this.speed);
    this.pos.add(dir);
  }
}

let grid;
let w = 10;
let cols, rows;
let hueValue = 1;

function setup() {
  colorMode(HSB, 360, 255, 255);
  createCanvas(windowWidth, windowHeight);
  cols = floor(width / w);
  rows = floor(height / w);
  grid = make2DArray(cols, rows);

  for (let i = 0; i < cols; i++) {
    for (let j = 0; j < rows; j++) {
      grid[i][j] = 0;
    }
  }
  grid[10][10] = 1;
  print(cols, rows);
}

function draw() {
  background(0);
  noStroke();
  for (let i = 0; i < cols; i++) {
    for (let j = 0; j < rows; j++) {
      if (grid[i][j] > 0) {
        fill(grid[i][j], 160, 255);
        rect(i * w, j * w, w, w);
      }
    }
  }

  let nextGrid = make2DArray(cols, rows);

  for (let i = 0; i < cols; i++) {
    for (let j = 0; j < rows; j++) {
      let state = grid[i][j];
      if (state > 0) {
        let below = grid[i][j + 1];
        let dir = random([1, -1]);
        let belowA, belowB;
        if (i + dir >= 0 && i + dir <= cols - 1) {
          belowB = grid[i + dir][j + 1];
        }
        if (i - dir >= 0 && i - dir <= cols - 1) {
          belowA = grid[i - dir][j + 1];
        }
        if (below === 0) {
          nextGrid[i][j + 1] = state;
        } else if (belowA === 0) {
          nextGrid[i - dir][j + 1] = state;
        } else if (belowB === 0) {
          nextGrid[i + dir][j + 1] = state;
        } else {
          nextGrid[i][j] = state;
        }
      }
    }
  }
  grid = nextGrid;
}

function mouseDragged() {
  let mouseCol = floor(mouseX / w);
  let mouseRow = floor(mouseY / w);

  let matrix = 2;
  let extent = floor(matrix / 2);
  for (let i = -extent; i <= extent; i++) {
    for (let j = -extent; j <= extent; j++) {
      let col = mouseCol + i;
      let row = mouseRow + j;
      if (col >= 0 && col < cols && row >= 0 && row < rows) {
        if (grid[col][row] === 0) {
          grid[col][row] = hueValue;
        }
      }
    }
  }
  hueValue += 1;
  if (hueValue > 360) {
    hueValue = 1;
  }
}

function make2DArray(cols, rows) {
  let arr = new Array(cols);
  for (let i = 0; i < arr.length; i++) {
    arr[i] = new Array(rows).fill(0);
  }
  return arr;
}

