let lines = [];

const PEN_SIZE = 4;
let prevPoint;
function setup() {
  createCanvas(windowWidth, windowHeight);
}

function keyPressed() {
  if (key == "r") {
    lines = []
  } 
}

function brushUp() {
  prevPoint = null;
  const lastLine = lines[lines.length - 1]
  if (lastLine) {

  lastLine.distance = null
  }
}

function mouseReleased() {
  brushUp()
}

function touchEnded() {
  brushUp()
}

function mouseDragged() {
  let currPoint = createVector(mouseX, mouseY);
  if (!prevPoint) {
    prevPoint = currPoint;
    return;
  }

  const distance = dist(prevPoint.x, prevPoint.y, currPoint.x, currPoint.y) / 5
  const lastLine = lines[lines.length - 1]
  let size = PEN_SIZE;
  if (lastLine && lastLine.distance) {
    if (distance > lastLine.distance) {
      size = lastLine.size + 1.5
    } else {
      size = lastLine.size - 1.5
    }
  }

  const l = {
    distance,
    size: constrain(size, PEN_SIZE , PEN_SIZE*2),
    p1: prevPoint,
    p2: currPoint
  }
  lines.push(l);
  prevPoint = currPoint;
}

function draw() {
  background(255);
  strokeWeight(2)
  // stroke(0, 100)
  for (let l of lines) {
    strokeWeight(l.size)
    const p1 = l.p1
    const p2 = l.p2
    line(p1.x, p1.y, p2.x, p2.y)
  }

  // Draw eraser
  rect(0, 0,  50,50)
  }

function touchStarted() {
  if (mouseX < 50 && mouseY < 50) {
    lines =[]
  }

}

