#ifdef GL_ES
precision mediump float;
#endif

attribute vec3 aPosition;
attribute vec2 aTexCoord;

// Always include this to get the position of the pixel and map the shader correctly onto the shape

void main() {

  // Copy the position data into a vec4, adding 1.0 as the w parameter
  vec4 positionVec4 = vec4(aPosition, 1.0);

  // Scale to make the output fit the canvas
  positionVec4.xy = positionVec4.xy * 2.0 - 1.0; 

  // Send the vertex information on to the fragment shader
  gl_Position = positionVec4;
}

#ifdef GL_ES
precision mediump float;
#endif

#define PI 3.14159265359
#define TWO_PI 6.28318530718

uniform vec2 resolution; // This is passed in as a uniform from the sketch.js file
uniform float time;
uniform float mouse;

vec3 rgb(float r, float g, float b) {
  return vec3(r/255.0, g/255.0, b/255.0);
}


vec4 poly(float x, float y, float size, float sides, float rotation, vec3 col) {
  vec2 coord = gl_FragCoord.xy;

  vec2 pos = vec2(x, y) - coord * 2.0;

  float angle = atan(pos.x, pos.y) + PI + rotation;

  float radius = TWO_PI / sides;

  float d = cos(floor(0.5 + angle/radius) * radius - angle) * length(pos);

  d = 1.0 - smoothstep(size*0.5, size*0.5+1.0, d);

  return vec4(col, d);
}


void main() {

  vec2 center = resolution;
