let l1, l2, points, c;
const nums = 50;
let hue = 0;
function setup() {
  createCanvas(800, 800);
  l1 = createVector(random(width), random(height));
  l2 = createVector(random(width), random(height));
  while (dist(l1.x, l1.y, l2.x, l2.y) < 400) {
    print("measuring...");
    l1 = createVector(random(width), random(height));
    l2 = createVector(random(width), random(height));
  }
  c = createVector(random(width), random(height));
  hue = random() * 360
  colorMode(HSL)
}

function keyPressed() {
  if (key == "r") {
    l1 = createVector(random(width), random(height));
    l2 = createVector(random(width), random(height));
    hue = random() * 360
    while (dist(l1.x, l1.y, l2.x, l2.y) < 400) {
      print("measuring...");
      l1 = createVector(random(width), random(height));
      l2 = createVector(random(width), random(height));
    }
  }
}

function draw() {
  background(220);
  strokeWeight(2);
  // drawing lines
  stroke(0);
  line(c.x, c.y, l1.x, l1.y);
  line(c.x, c.y, l2.x, l2.y);

  c = createVector(mouseX, mouseY);

  // drawing points of each line
  points1 = getPoints(l1, c, nums);
  points2 = getPoints(l2, c, nums);

  strokeWeight(4);
  stroke(0);
  for (const p of points1) {
    point(p.x, p.y);
  }

  for (const p of points2) {
    point(p.x, p.y);
  }

  // drawing curve lines
  strokeWeight(1);
  stroke(hue, 50, 50, 200);
  for (let i = 0; i < nums; i++) {
    line(
      points1[i].x,
      points1[i].y,
      points2[nums - i - 1].x,
      points2[nums - i - 1].y
    );
  }
}

function getPoints(l1, l2, n) {
  const points = [];
  // const d = dist(l1.x, l1.y, l2.x, l2.y)
  // const n = floor(d / 20)
  const deltaX = (l2.x - l1.x) / (n + 1);
  const deltaY = (l2.y - l1.y) / (n + 1);
  for (let i = 0; i <= n; i++) {
    const x = l1.x + deltaX * i;
    const y = l1.y + deltaY * i;
    points.push(createVector(x, y));
  }
  return points;
}

