function toggleFilter() {
  if (currFilterText === "NORMAL") {
    changeFilter(0)
  } else {
    filteredImage = img.get()
    currFilterText = "NORMAL"
    updateText()
  }
}

function saveImage() {
  filteredImage.save(currFilterText, "png");
}
function mod(n, m) {
  return ((n % m) + m) % m;
}

function changeFilter(n) {
  filterIndex = mod(filterIndex + n, 9);
  print(filterIndex)
  filteredImage = img.get();
  switch (filterIndex) {
    case 0:
      filteredImage.filter(INVERT);
      currFilterText = "INVERT";
      break;
    case 1:
      filteredImage.filter(THRESHOLD);
      currFilterText = "THRESHOLD";
      break;
    case 2:
      filteredImage.filter(THRESHOLD);
      filteredImage.filter(INVERT);
      currFilterText = "THRESHOLD INVERT";
      break;
    case 3:
      filteredImage.filter(GRAY);
      currFilterText = "GRAY";
      break;
    case 4:
      filteredImage.filter(POSTERIZE);
      currFilterText = "POSTERIZE";
      break;
    case 5:
      filteredImage.filter(BLUR, 4);
      currFilterText = "BLUR";
      break;
    case 6:
      filteredImage.filter(ERODE);
      currFilterText = "ERODE";
      break;
    case 7:
      filteredImage.filter(DILATE);
      currFilterText = "DILATE";
      break;
    default:
      currFilterText = "NORMAL";
  }
  print(currFilterText);
  updateText()
}

function updateText(newText) {
  filterP.elt.textContent = currFilterText

}

function imageLoaded() {
  const canvasAspectRatio = fullWidth / fullHeight;
  const imageAspectRatio = img.width / img.height;

  let scaleFactorWidth = 1;
  let scaleFactorHeight = 1;

  if (imageAspectRatio > canvasAspectRatio) {
    scaleFactorWidth = fullWidth / img.width;
  } else {
    scaleFactorHeight = fullHeight / img.height;
  }

  const scaleFactor = min(scaleFactorWidth, scaleFactorHeight);
  imgWidth = img.width * scaleFactor;
  imgHeight = img.height * scaleFactor;
  resizeCanvas(imgWidth, imgHeight);

  filteredImage = img.get();
  currFilterText = "NORMAL"
  updateText()
}

// Create an image if the file is an image.
function handleImage(file) {
  if (file.type === "image") {
    img = loadImage(file.data, imageLoaded);
  } else {
    img = null;
    filteredImage = null;
  }
}

