const res = 10;
let cols, rows;
let field = [];
let noise;
let incr = 0.1
let zoff = 0

function setup() {
  createCanvas(windowWidth, windowHeight);
  noise = new OpenSimplexNoise(Date.now());
  cols = floor(width / res) + 1;
  rows = floor(height / res) + 1;
  field = make2DArray(cols, rows);
  for (let i = 0; i < cols; i++) {
    for (let j = 0; j < rows; j++) {
      field[i][j] = floor(random(2));
    }
  }
}

function draw() {
  background(0);

  let xoff = 0
  zoff += 0.01
  for (let i = 0; i < cols; i++) {
    xoff += incr
    let yoff = 0
    for (let j = 0; j < rows; j++) {
      yoff += incr
      field[i][j] = noise.noise3D(xoff, yoff, zoff);
    }
  }
  for (let i = 0; i < cols; i++) {
    for (let j = 0; j < rows; j++) {
      strokeWeight(res * 0.5);
      if (field[i][j] > 0) {
        fill(255 )
      } else {
        fill(0)
      }
      noStroke()
      // fill(field[i][j] * 255);
      // fill(0)
      // rect(i * res, j * res, res, res);
    }
  }
  for (let i = 0; i < cols - 1; i++) {
    for (let j = 0; j < rows - 1; j++) {
      let x = i * res;
      let y = j * res;
      let a = createVector(x + res * 0.5, y);
      let b = createVector(x + res, y + res * 0.5);
      let c = createVector(x + res * 0.5, y + res);
      let d = createVector(x, y + res * 0.5);

      let state = getState(
        ceil(field[i][j]),
        ceil(field[i + 1][j]),
        ceil(field[i + 1][j + 1]),
        ceil(field[i][j + 1])
      );

      stroke(255);
      strokeWeight(1);
      switch (state) {
        case 1:
          drawLine(c, d);
          break;
        case 2:
          drawLine(b, c);
          break;
        case 3:
          drawLine(b, d);
          break;
        case 4:
          drawLine(a, b);
          break;
        case 5:
          drawLine(a, d);
          drawLine(b, c);
          break;
        case 6:
          drawLine(a, c);
          break;
        case 7:
          drawLine(a, d);
          break;
        case 8:
          drawLine(a, d);
          break;
        case 9:
          drawLine(a, c);
          break;
        case 10:
          drawLine(a, b);
          drawLine(c, d);
          break;
        case 11:
          drawLine(a, b);
          break;
        case 12:
          drawLine(b, d);
          break;
        case 13:
          drawLine(b, c);
          break;
        case 14:
          drawLine(c, d);
          break;
      }
      // line(a.x, a.y, b.x, b.y);
      // line(a.x, a.y, c.x, c.y);
      // line(a.x, a.y, d.x, d.y);
      // line(c.x, c.y, b.x, b.y);
      // line(d.x, d.y, b.x, b.y);
      // line(d.x, d.y, c.x, c.y);
    }
  }
}
function drawLine(v1, v2) {
  line(v1.x, v1.y, v2.x, v2.y);
}
function getState(a, b, c, d) {
  return a * 8 + b * 4 + c * 2 + d * 1;
}

function make2DArray(cols, rows) {
  let arr = new Array(cols);
  for (let i = 0; i < arr.length; i++) {
    arr[i] = new Array(rows).fill(0);
  }
  return arr;
}

