  vector = createVector(random(width), random(height)); // create a random vector
  angle = random(TWO_PI); // create a random angle
  len = 0; // set the initial height of the line to zero
  lineLength = random(200)
  speed = 20; // set the speed of the animation to 2 pixels per frame
  background(200)
}

function draw() {
  // background(220);
  strokeWeight(4);
  push(); // save the current transformation state
  translate(vector.x, vector.y); // move the origin to the vector position
  rotate(angle); // rotate the coordinate system by the angle
  line(0, 0, 0, -len); // draw a line from (0, 0) to (0, -height)
  pop(); // restore the previous transformation state
  len += speed; // increment the height by the speed
  if (len > lineLength) {
    vector = createVector(random(width), random(height))
    len = 0
    angle = random(TWO_PI)
    lineLength = random(50, 200)
  }
}

