function draw() {
  background(51);
  scale(vWidth/video.width)

  push();
  translate(video.width, 0);
  scale(-1.0, 1.0); // flip x-axis backwards
  image(video, 0, 0, video.width, video.height); //video on canvas, position, dimensions
  pop();
  if (
    pose &&
    pose.leftEye.confidence > 0.99 &&
    pose.rightEye.confidence > 0.99
  ) {
    fill(255, 0, 0);

    let left = createVector(pose.leftEye.x, pose.leftEye.y);
    let right = createVector(pose.rightEye.x, pose.rightEye.y);
    angleMode(DEGREES);
    // let a = left.angleBetween(right)
    let a = angleBetweenTwoPoints(
      pose.leftEye.x,
      pose.leftEye.y,
      pose.rightEye.x,
      pose.rightEye.y
    );

    let d = dist(
      pose.leftEye.x,
      pose.leftEye.y,
      pose.rightEye.x,
      pose.rightEye.y
    );

    let size = d * sizeOff;
    // let gx = pose.leftEye.x * xoff;
    // let gy = pose.leftEye.y * yoff;
    let gx = ((pose.leftEye.x + pose.rightEye.x) / 2) * xoff;
    let gy = ((pose.leftEye.y + pose.rightEye.y) / 2) * yoff;
    push();
    translate(gx, gy);
    rotate(a);
    imageMode(CENTER);
    image(img, 0, 0, size, size / 2);
    pop();

    ellipse(pose.leftEye.x, pose.leftEye.y, 15);
    ellipse(pose.rightEye.x, pose.rightEye.y, 15);
  }
