class Branch {
  constructor(begin, end) {
    this.begin = begin
    this.end = end
    this.grown = false
  }

  show() {
    stroke(255)
    line(this.begin.x, this.begin.y, this.end.x, this.end.y)
  }

  jitter() {
    this.end.x += random(-1, 1)
    this.end.y += random(-1, 1)
  }

  branch() {
    if (this.grown) {
      return []
    }
    this.grown = true
    let dirRight = p5.Vector.sub(this.end, this.begin)
    dirRight.rotate(PI/4 + random(-.5, .5))
    dirRight.mult(0.67)
    let newEndRight = p5.Vector.add(this.end , dirRight)
    let right = new Branch(this.end, newEndRight)

    let dirLeft = p5.Vector.sub(this.end, this.begin)
    dirLeft.rotate(-PI/4 + random(-.5, .5))
    dirLeft.mult(0.67)
    let newEndLeft = p5.Vector.add(this.end , dirLeft)
    let left = new Branch(this.end, newEndLeft)


    return [left, right]
  }
}

