let saveBtn;

function setup() {
  colorPicker = createColorPicker("#000000");
  resetBtn = createButton("Reset");
  saveBtn = createButton("Save");

  resetBtn.mousePressed(() => {
    pixelsArr = new Array(res * res).fill(255);
    drawPixels();
  });

  saveBtn.mousePressed(() => {
    saveCanvas();
  });
  const canvasDim = floor(min(windowWidth, windowHeight) / res) * res - 100;

  createCanvas(canvasDim, canvasDim);
  edge = floor(min(width, height) / res);
  console.log(edge);
  drawPixels();
}

// function draw() {
//   // background(0)
//   mouseToGridPos()
// }

function mouseClicked() {
  const [x, y] = mouseToGridPos();
  const index = gridToArrayIndex(x, y);
  if (pixelsArr[index] != 255) {
    toPaint = false;
  } else {
    toPaint = true;
  }
  togglePixels(toPaint);
}

function mouseDragged() {
  togglePixels(toPaint);
}

function gridToArrayIndex(x, y) {
  return x * res + y;
}

function mouseToGridPos() {
  let mX = mouseX;
  let mY = mouseY;

  if (mX < 0 || mX > width || mY < 0 || mY > height) return [null, null];
  let x = floor(mouseX / edge);
  let y = floor(mouseY / edge);

  // Calculating center of rect
  let rectX = x * edge + edge / 2;
  let rectY = y * edge + edge / 2;
  // ellipse(rectX, rectY, 30)
  let distX = abs(mX - rectX);
  let distY = abs(mY - rectY);
  // console.log(distX, distY)
  if (distX < edge / 3 && distY < edge / 3 && x >= 0 && y >= 0) {
    // console.log("yes")
    return [x, y];
  } else {
    return [null, null];
  }
}

function togglePixels(toPaint) {
  const [x, y] = mouseToGridPos();
  if (x == null || y == null) return;
  const i = gridToArrayIndex(x, y);
  if (i > res * res || i < 0) {
    return;
  }
  if (toPaint) {
    pixelsArr[i] = colorPicker.color();
  } else {
    pixelsArr[i] = 255;
  }
  drawPixels();
}

function drawPixels() {
  for (let i = 0; i < pixelsArr.length; i++) {
    fill(pixelsArr[i]);
    let y = i % res;
    let x = floor(i / res);
    rect(x * edge, y * edge, edge, edge);
  }
}

