let resolution;
let grid;
let cols;
let rows;
let stopBtn;
let clearBtn;
let isPlaying = true;

function setup() {
  createCanvas(windowWidth, windowHeight*0.9);

  stopBtn = createButton("Stop")
  stopBtn.mousePressed(stopBtnPressed)

  clearBtn = createButton("Clear")
  clearBtn.mousePressed(clearBtnPressed)

  resolution = 20;
  cols = floor(width / resolution);
  rows = floor(height / resolution);
  grid = make2DArray(cols, rows);
  for (let i = 0; i < cols; i++) {
    for (let j = 0; j < rows; j++) {
      grid[i][j] = floor(random(2));
    }
  }
  noStroke();
}

function mouseClicked() {
  toggleCell()
}

function mouseDragged() {
  toggleCell()
}

function toggleCell() {
  let x = floor(mouseX / resolution)
  let y = floor(mouseY / resolution)
  if (x < 0 || y < 0 || x > cols - 1 || y > rows - 1) {
    return
  }
  if (grid[x][y] == 0) {
    grid[x][y] = 1
  } else {
    grid[x][y] = 0
  }
}
function stopBtnPressed() {
  isPlaying = !isPlaying
  if (isPlaying) {
    stopBtn.elt.textContent = "Stop"
  } else {
    stopBtn.elt.textContent = "Start"
  }
}
function clearBtnPressed() {
  for (let i = 0; i < cols; i ++) {
    for (let j = 0; j < rows; j++) {
      grid[i][j] = 0
    }
  }
}

function draw() {
  background(50);

  for (let i = 0; i < cols; i++) {
    for (let j = 0; j < rows; j++) {
      let x = i * resolution;
      let y = j * resolution;
      if (grid[i][j] == 1) {
        fill(255);
      } else {
        fill(0);
      }
      if (mouseX < x + resolution && mouseY < y + resolution && mouseX > x && mouseY > y) {
        fill(0, 255, 0);
      }
      rect(x, y, resolution - 1, resolution - 1);
    }
  }

  if (!isPlaying) {
    return
  }

  let next = make2DArray(cols, rows);
  // print(next);
  // Compute next based on grid
  for (let i = 0; i < cols; i++) {
    for (let j = 0; j < rows; j++) {
      let state = grid[i][j];
      let neighbors = countNeighbors(grid, i, j);
      if (state == 0 && neighbors == 3) {
        next[i][j] = 1;
      } else if (state == 1 && (neighbors < 2 || neighbors > 3)) {
        next[i][j] = 0;
      } else {
        next[i][j] = state;
      }
    }
  }
  grid = next;
}

function countNeighbors(grid, x, y) {
  let sum = 0;
  for (let i = -1; i < 2; i++) {
    for (let j = -1; j < 2; j++) {
      let col = (cols + x + i) % cols;
      let row = (rows + y + j) % rows;

      sum += grid[col][row];
    }
  }
  sum -= grid[x][y];
  return sum;
}

function make2DArray(cols, rows) {
  let arr = new Array(cols);
  for (let i = 0; i < arr.length; i++) {
    arr[i] = new Array(rows).fill(0);
  }
  return arr;
}

