let inputFile;
let song;
let amp;
let stop;
let len;
// let button;
let rainbow = 0;
let clrIncr = 0.2;

let innerR;

function setup() {
  frameRate(24)
  createCanvas(windowWidth, windowHeight);
  len = min(width, height);
  radiusSlider = createSlider(0, len / 3, len / (3 * 3));
  radiusSlider.position(0, height - 25);

  forward = createButton(">>")
  backward = createButton("<<")
  forward.position(width/2 + 25, height-25)
  backward.position(width/2 - 25, height-25)

  forward.mousePressed(()=> {
    if (song) {
      song.jump(song.currentTime() + 5)
    }  
  })
  backward.mousePressed(()=> {
    if (song) {
      song.jump(song.currentTime() - 5)
    }  
  })
  stop = createButton("pause");
  //   button = createButton("start")
  //   button.position()

  //   button.mousePressed(() => {
  //     userStartAudio()
  //   })
  stop.position(width - 55, height - 25);
  stop.mousePressed(() => {
    if (song.isPlaying()) song.stop();
  });
  inputFile = createFileInput((file) => {
    if (song) {
      song.stop();
      console.log("song stopping?");
    }
    if (file.type == "audio") {
      song = loadSound(file.data, () => {
        song.loop();
        song.setVolume(1);
      });
    } else {
      song = null;
      // seekSlider = null;
    }
  });
  inputFile.position(0, 0);
  angleMode(DEGREES);
  fft = new p5.FFT(0.8, 32);
  fft.setInput(song);
  amp = new p5.Amplitude();
}

function draw() {
  colorMode(RGB);
  background(0);
  let spectrum = fft.analyze();
  translate(width / 2, height / 2);
  stroke(255);
  rotate(degree);
  if (song) {
    degree = map(song.currentTime(), 0, song.duration(), 0, 360);
  }
  if (degree < 0) degree = 360;
  for (let i = 0; i < spectrum.length; i++) {
    let angle = map(i, 0, spectrum.length, 0, 360);
    let amplitude = spectrum[i];
    let r = map(amplitude, 0, 255, 0, len / 3);
    // r = len/3
    let x = r * cos(angle);
    let y = r * sin(angle);
    // hue = map(angle, 0, 360, rainbow, rainbow + 32);
    rainbow += clrIncr;
    if (rainbow > 255 || rainbow < 0) {
      clrIncr *= -1
      rainbow = random(255);
    }
    colorMode(HSB);
    let strokeColor = color(rainbow + spectrum[i], 255, 255);
    stroke(strokeColor);
    // hue += 2
    // if (hue > 255) {
    //   hue = 0
    // }
    strokeWeight(8);
    // strokeCap(SQUARE);

    let level = amp.getLevel();
    let innerR = map(level, 0, 1, radiusSlider.value(), len / 4);
    if (r > innerR) {
      line(innerR * cos(angle), innerR * sin(angle), x, y);
    }
  }
}

