  let y = mouseY
  if (abs(x - touchX) > abs(y - touchY)) {
    // user swiped in x direction
    if ((x - touchX) < 0) {
      // user swiped LEFT
      snake.setDir(-1, 0)
    } else {
      snake.setDir(1, 0)
    }
  } else {
    if ((y - touchY) < 0) {
      // user swiped UP
      snake.setDir(0, -1)
    } else {
      snake.setDir(0, 1)
    }
  }
}

function keyPressed() {
  if (key === 'k') {
    snake.setDir(0, -1);
  } else if (key === 'j') {
    snake.setDir(0, 1);
  } else if (key === 'h') {
    snake.setDir(-1, 0);
  } else if (key === 'l') {
    snake.setDir(1, 0);
  }
  if (key === ' ') {
    snake.grow()
  }
}

