// Flow / Variable

let circleX = 10;

function setup() {
  createCanvas(400, 300);
}
function draw() {
  background(0);
  noStroke();
  fill(255);
  ellipse(circleX, 150, 50);
  circleX++;
}

function mousePressed() {
  circleX = 10;
}

