// The Coding Train
// Lauren McCarthy and Cassie Tarakajian (creator of the editor)
function setup() {
  createCanvas(400, 400);
}

function draw() {
  background(220, 0, 200);
  line(0, 0, 400, 400);

  // Rectangular Face
  rectMode(CENTER);
  rect(200, 200, 300, 300, 80, 20, 200, 200);

  // Right Eye
  stroke(2);
  ellipse(130, 135, 100);

  // Left Eye

  ellipseMode(CENTER);
  strokeWeight(10)
  ellipse(260, 130, 70);

  // Mouth
  arc(180, 240, 100, 100, 45, 60);

}

